<?phpfunction gotoURL($url,$delay){	echo "<meta http-equiv=\"refresh\" content=\"$delay; url=$url\" />";}function alert($text){	echo "<script>alert('".$text."')</script>";}function utf8_strlen($s) {    		$c = strlen($s); $l = 0;		for ($i = 0; $i < $c; ++$i) if ((ord($s[$i]) & 0xC0) != 0x80) ++$l;		return $l;}//------ เข้าสู่ระบบ แอดมิน------------//if($_GET['action']=="adminLogin"){		$f_user = $_POST['f_user'];	$f_pass = $_POST['f_pass'];							$sql = mysqli_query($GLOBALS['CON'],"SELECT * FROM `".$table_prefix."config` WHERE `username` LIKE '$f_user' AND `password` LIKE '$f_pass' ");	$rs=mysqli_fetch_array($sql);	 	if(mysqli_num_rows($sql)>0){		$alert_text="กำลังเข้าสู่โหมดผู้ดูแลระบบ";		$_SESSION['ss_admin_id']=$rs['id'];		gotoURL("$PATH_URL/adminMenu",2);	}else{		$alert_text="<span class='text-red'>Username</span> หรือ <span class='text-red'>Password</span> ไม่ถูกต้อง กรุณาเข้าสู่ระบบอีกครั้ง";		gotoURL("$PATH_URL/login",2);	}}if($_GET['action']=="adminLogout"){	$alert_text="กำลังออกจากระบบ";	session_destroy();	gotoURL("$PATH_URL/login",3);	}	 ?>