<?php //------------------------QUERY---------------------------//function get_post_title($id){	global $table_prefix;	$sql=mysqli_query($GLOBALS['CON'],"SELECT * FROM `".$table_prefix."post` WHERE `post_id` = $id");	$rs=mysqli_fetch_array($sql);	return urldecode($rs['post_title']);	}function get_last_post(){	global $table_prefix;	$sql=mysqli_query($GLOBALS['CON'],"SELECT * FROM `".$table_prefix."post` ORDER BY `".$table_prefix."post`.`post_id` DESC LIMIT 0,1");	$rs=mysqli_fetch_array($sql);	return $rs['post_id'];	}function get_post_cover($post_id){	global $table_prefix;	$sql=mysqli_query($GLOBALS['CON'],"SELECT * FROM `".$table_prefix."post` WHERE `post_id` = $post_id");	$rs=mysqli_fetch_array($sql);	if($rs['post_cover']!=""){		return $rs['post_cover'];		}else{		return "default_cover.jpg";	}}function update_view($post_id){	global $table_prefix;		$sql=mysqli_query($GLOBALS['CON'],"SELECT * FROM `".$table_prefix."post` WHERE `post_id` = $post_id");	$rs=mysqli_fetch_array($sql);	$c=$rs['post_view']+=1;		mysqli_query($GLOBALS['CON'],"UPDATE `".$table_prefix."post` SET `post_view` = '$c' WHERE `".$table_prefix."post`.`post_id` = $post_id");}function get_post_slug_by_post_id($id){	global $table_prefix;		$sql=mysqli_query($GLOBALS['CON'],"SELECT `post_slug` FROM `".$table_prefix."post` WHERE `post_id` = $id");	$rs=mysqli_fetch_array($sql);	return $rs['post_slug'];	}function get_post_cate_by_post_id($id){	$sql=mysqli_query($GLOBALS['CON'],"SELECTpost.post_typeFROMpostWHEREpost.post_id = $id");	$rs=mysqli_fetch_array($sql);	return $rs['post_type'];	}function get_cate_name_by_post_id($id){	$sql=mysqli_query($GLOBALS['CON'],"SELECTpost_cate.cate_nameFROMpostINNER JOIN post_cate ON post.post_type = post_cate.cate_idWHEREpost.post_id = $id");	$rs=mysqli_fetch_array($sql);	return $rs['cate_name'];	}function get_cate_name_by_cate_id($id){	$sql=mysqli_query($GLOBALS['CON'],"SELECTpost_cate.cate_nameFROMpost_cateWHEREpost_cate.cate_id = $id");	$rs=mysqli_fetch_array($sql);	return $rs['cate_name'];	}function get_cate_id_by_cate_name($cate_name){	$sql=mysqli_query($GLOBALS['CON'],"SELECTpost_cate.cate_idFROMpost_cateWHEREpost_cate.cate_name = '$cate_name' ");	$rs=mysqli_fetch_array($sql);	return $rs['cate_id'];	}function get_cate_id_by_post_id($id){	global $table_prefix;	$sql=mysqli_query($GLOBALS['CON'],"SELECT `post_cate` FROM `".$table_prefix."post` WHERE `post_id` = $id");	$rs=mysqli_fetch_array($sql);	return $rs['post_cate'];	}function count_post_in_cate($cate){	global $table_prefix;	$sql=mysqli_query($GLOBALS['CON'],"SELECT * FROM `".$table_prefix."post` WHERE `post_type` LIKE 'service' AND `post_cate` = $cate");	return mysqli_num_rows($sql);}//------------------------SELECT---------------------------//function get_all_tag(){	global $table_prefix;	$sql=mysqli_query($GLOBALS['CON'],"SELECT".$table_prefix."post.post_tagFROM".$table_prefix."postWHERE".$table_prefix."post.post_tag NOT LIKE '' ");	return mysqli_fetch_array($sql);}//------------------------INSERT---------------------------//if($action=="editPage"){			mysqli_query($GLOBALS['CON'],"UPDATE `".$table_prefix."post`     SET `post_content` = '".urlencode($f_content)."',        `post_title` = '$f_name'    WHERE `".$table_prefix."post`.`post_id` = $post_id;");	$alert_text="แก้ไขข้อมูลแล้ว";		gotoURL("$PATH_URL/?p=page-edit&post_id=$post_id",0);}if($action=="addProduct"){		$ext=strtolower(end(explode('.',$_FILES['f_image']['name'])));	$images = $_FILES['f_image']['tmp_name'];			$new_images = date("YmdHis").".".$ext;	$width=600; //*** Fix Width & Heigh (Autu caculate) ***//	$size=GetimageSize($images);	$height=round($width*$size[1]/$size[0]);	$images_orig = ImageCreateFromJPEG($images);	$photoX = ImagesX($images_orig);	$photoY = ImagesY($images_orig);	$images_fin = ImageCreateTrueColor($width, $height);	ImageCopyResampled($images_fin, $images_orig, 0, 0, 