ction=="addPortfolio"){		$ext=strtolower(end(explode('.',$_FILES['f_image']['name'])));	$images = $_FILES['f_image']['tmp_name'];			$new_images = date("YmdHis").".".$ext;	$width=600; //*** Fix Width & Heigh (Autu caculate) ***//	$size=GetimageSize($images);	$height=round($width*$size[1]/$size[0]);	$images_orig = ImageCreateFromJPEG($images);	$photoX = ImagesX($images_orig);	$photoY = ImagesY($images_orig);	$images_fin = ImageCreateTrueColor($width, $height);	ImageCopyResampled($images_fin, $images_orig, 0, 0, 0, 0, $width+1, $height+1, $photoX, $photoY);	ImageJPEG($images_fin,"img-post/".$new_images);	ImageDestroy($images_orig);	ImageDestroy($images_fin);				$sql="INSERT INTO `".$table_prefix."post`(        `post_id`,        `post_title`,        `post_type`,        `post_cate`,        `post_content`,		post_brand,		post_model,        `post_vdo`,        `post_price`,        `post_view`,        `post_cover`,        `post_date`    )VALUES	(NULL,	 '$f_name',	'portfolio',	'0',	'".urlencode($f_content)."',	'0',	'0',	'0',	'0', 	'0', 	'$new_images', 	'$f_date' );";	mysqli_query($GLOBALS['CON'],$sql);		$last_post=get_last_post();	gotoURL("$PATH_URL/?p=page-portfolio-edit&post_id=$last_post",0);	exit();	}if($action=="addBanner"){		$ext=strtolower(end(explode('.',$_FILES['f_image']['name'])));	$images = $_FILES['f_image']['tmp_name'];			$new_images = date("YmdHis").".".$ext;	$width=1600; //*** Fix Width & Heigh (Autu caculate) ***//	$size=GetimageSize($images);	$height=round($width*$size[1]/$size[0]);	$images_orig = ImageCreateFromJPEG($images);	$photoX = ImagesX($images_orig);	$photoY = ImagesY($images_orig);	$images_fin = ImageCreateTrueColor($width, $height);	ImageCopyResampled($images_fin, $images_orig, 0, 0, 0, 0, $width+1, $height+1, $photoX, $photoY);	ImageJPEG($images_fin,"img-post/".$new_images);	ImageDestroy($images_orig);	ImageDestroy($images_fin);			mysqli_query($GLOBALS['CON'],"INSERT INTO `".$tab